document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll("a[href^='#']").forEach(e=>{e.addEventListener("click",(function(e){e.preventDefault(),function(e){let t=document.getElementById(e),o=document.getElementById("header").offsetHeight,n=t.offsetTop-o;window.scrollTo({top:n,behavior:"smooth"})}(this.hash.replace("#",""))}))})}));